
package operator;

public class BitwiseOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=4;
		int c=a&b;
		int d=a|b;
		int e=b>>1;
		int f=b<<2;
		System.out.println(c);
		System.out.println(d);
		System.out.println(e);
		System.out.println(f);
		

	}

}
