package operator;

public class SwitchCondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int weakOfTheDay=4;
switch(weakOfTheDay)
{
case 1:
	System.out.println("i am on the leave");
	break;

	case 2:
		System.out.println("i am in the office");
		break;
	case 3:
		System.out.println("i am in the college");
		break;
	case 4:
		System.out.println("i am playing football");
		break;
		default:
			System.out.println("invalid");
	}

}
}
